package com.example.r6siegestats.r6sstatsapi.external.objects.respones;

import lombok.Data;

import java.util.Date;

@Data
public class UbiProfileResponse {
    private String userId;
    private String username;
    private String nameOnPlatform;
    private Boolean accountIssues;
    private Boolean communicationOptIn;
    private Boolean communicationThirdPartyOptIn;
    private String country;
    private Date dateCreated;
    private Date dateOfBirth;
    private String email;
    private String firstName;
    private String gender;
    private Boolean hasAcceptedLegalOptions;
    private String lastName;
    private String preferredLanguage;
    private Status status;
    private Phone phone;
    private String accountType;

    @Data
    public class Status {
        private Boolean autoGeneratedUsername;
        private Boolean dateOfBirthApproximated;
        private Boolean invalidEmail;
        private Boolean missingRequiredInformation;
        private Boolean pendingDeactivation;
        private Boolean targetDeactivationDate;
        private Boolean recoveringPassword;
        private Boolean passwordUpdateRequired;
        private Boolean reserved;
        private Boolean changeEmailPending;
        private Boolean inactiveAccount;
        private Boolean generalStatus;
        private Boolean suspiciousActivity;
        private Boolean locked;
        private Boolean minor;

        // didn't have 'minor', but had next to variables
        // "testAccount": boolean,
        // "phoneActivated": boolean
    }

    @Data
    public class Phone {
        private String number;
        private String countryCallingCode;
    }
}
